import React, { useEffect, useState } from 'react';
import { Student, Teacher, Course, Enrollment } from './types';
import * as api from './api';

import StudentList from './component/StudentList';
import TeacherList from './component/TeacherList';
import CourseList from './component/CourseList';
import EnrollmentList from './component/EnrollmentList';

const App: React.FC = () => {
  const [students, setStudents] = useState<Student[]>([]);
  const [teachers, setTeachers] = useState<Teacher[]>([]);
  const [courses, setCourses] = useState<Course[]>([]);
  const [enrollments, setEnrollments] = useState<Enrollment[]>([]);
  const [activeTab, setActiveTab] = useState<'students' | 'teachers' | 'courses' | 'enrollments'>('students');

  useEffect(() => {
    refreshData();
  }, []);

  const refreshData = async () => {
    try {
      const [s, t, c, e] = await Promise.all([
        api.getStudents(), api.getTeachers(), api.getCourses(), api.getEnrollments()
      ]);
      setStudents(s); setTeachers(t); setCourses(c); setEnrollments(e);
    } catch (err) {
      console.error("Error loading data.", err);
    }
  };

  // Handlers for CRUD operations
  const handleAddStudent = async (data: any) => { await api.createStudent(data); refreshData(); };
  const handleUpdateStudent = async (id: number, data: any) => { await api.updateStudent(id, data); refreshData(); };
  const handleDeleteStudent = async (id: number) => { await api.deleteStudent(id); refreshData(); };

  const handleAddTeacher = async (data: any) => { await api.createTeacher(data); refreshData(); };
  const handleUpdateTeacher = async (id: number, data: any) => { await api.updateTeacher(id, data); refreshData(); };
  const handleDeleteTeacher = async (id: number) => { await api.deleteTeacher(id); refreshData(); };

  const handleAddCourse = async (data: any) => { await api.createCourse(data); refreshData(); };
  const handleUpdateCourse = async (id: number, data: any) => { await api.updateCourse(id, data); refreshData(); };
  const handleDeleteCourse = async (id: number) => { await api.deleteCourse(id); refreshData(); };

  const handleAddEnrollment = async (data: any) => { await api.createEnrollment(data); refreshData(); };
  const handleDeleteEnrollment = async (id: number) => { await api.deleteEnrollment(id); refreshData(); };

  return (
    <div className="flex min-h-screen bg-[#0f172a] text-slate-200 font-sans">
      {/* 1. Sidebar: Solid Black with Blue Accents */}
      <aside className="w-64 bg-black border-r border-blue-900/30 flex flex-col fixed h-full shadow-2xl">
        <div className="p-8 border-b border-blue-900/20">
          <h1 className="text-2xl font-black text-blue-500 tracking-tighter uppercase italic">
            USTP <span className="text-white">IT</span>
          </h1>
          <div className="h-1 w-12 bg-blue-600 rounded-full mt-1"></div>
        </div>

        <nav className="flex-1 p-4 space-y-3 mt-6">
          {[
            { id: 'students', label: 'Students', icon: 'ðŸ‘¤' },
            { id: 'teachers', label: 'Teachers', icon: 'ðŸ‘¨â€ðŸ«' },
            { id: 'courses', label: 'Courses', icon: 'ðŸ“š' },
            { id: 'enrollments', label: 'Enrollments', icon: 'ðŸ“' },
          ].map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id as any)}
              className={`w-full flex items-center gap-4 px-5 py-4 rounded-xl text-sm font-bold transition-all duration-300 ${
                activeTab === tab.id 
                ? 'bg-blue-700 text-white shadow-[0_0_20px_rgba(29,78,216,0.4)] translate-x-1' 
                : 'text-slate-500 hover:text-blue-400 hover:bg-blue-900/10'
              }`}
            >
              <span className="text-lg opacity-80">{tab.icon}</span> {tab.label}
            </button>
          ))}
        </nav>

        <div className="p-6 text-[10px] text-slate-600 border-t border-blue-900/20 uppercase tracking-widest font-bold">
          System Core 2026
        </div>
      </aside>

      {/* 2. Content: Deep Slate/Black with Blue Highlights */}
      <main className="flex-1 ml-64 p-10 bg-[#0f172a]">
        <div className="max-w-6xl mx-auto">

          <header className="flex justify-between items-end mb-12 border-b border-blue-900/20 pb-8">
            <div>
              <h2 className="text-4xl font-black text-white uppercase tracking-tight">
                {activeTab} <span className="text-blue-600">Portal</span>
              </h2>
              <p className="text-slate-400 mt-2 font-medium">Control panel for university {activeTab} records.</p>
            </div>
            <div className="text-right">
              <span className="text-blue-500 font-mono font-bold text-xs block mb-1">DATA NODES</span>
              <span className="text-3xl font-black text-white px-4 py-1 bg-blue-900/20 rounded-lg border border-blue-800/30">
                {activeTab === 'students' && students.length}
                {activeTab === 'teachers' && teachers.length}
                {activeTab === 'courses' && courses.length}
                {activeTab === 'enrollments' && enrollments.length}
              </span>
            </div>
          </header>

          {/* 3. Component Container */}
          <div className="bg-[#1e293b] rounded-2xl p-8 border border-blue-900/30 shadow-2xl shadow-black/50 transition-all duration-500 animate-in fade-in slide-in-from-right-4">
            {activeTab === 'students' && (
              <StudentList students={students} onAddStudent={handleAddStudent} onUpdateStudent={handleUpdateStudent} onDeleteStudent={handleDeleteStudent} />
            )}
            {activeTab === 'teachers' && (
              <TeacherList teachers={teachers} onAddTeacher={handleAddTeacher} onUpdateTeacher={handleUpdateTeacher} onDeleteTeacher={handleDeleteTeacher} />
            )}
            {activeTab === 'courses' && (
              <CourseList courses={courses} teachers={teachers} onAddCourse={handleAddCourse} onUpdateCourse={handleUpdateCourse} onDeleteCourse={handleDeleteCourse} />
            )}
            {activeTab === 'enrollments' && (
              <EnrollmentList enrollments={enrollments} students={students} courses={courses} onAddEnrollment={handleAddEnrollment} onDeleteEnrollment={handleDeleteEnrollment} />
            )}
          </div>

          <footer className="mt-20 text-center">
             <p className="text-slate-600 text-[11px] font-bold tracking-[0.2em] uppercase">
               &copy; 2026 USTP Information Technology Department â€¢ Cagayan de Oro
             </p>
          </footer>
        </div>
      </main>
    </div>
  );
};

export default App;
