import React, { useState } from 'react';
import { Course, Teacher } from '../types';

interface CourseListProps {
  courses: Course[];
  teachers: Teacher[];
  onAddCourse: (course: any) => Promise<void>;
  onUpdateCourse: (id: number, data: any) => Promise<void>;
  onDeleteCourse: (id: number) => Promise<void>;
}

export const CourseList: React.FC<CourseListProps> = ({ courses, teachers, onAddCourse, onUpdateCourse, onDeleteCourse }) => {
  const [form, setForm] = useState({ course_name: '', units: '', teacher: '' });
  const [editingId, setEditingId] = useState<number | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const data = { 
      course_name: form.course_name, 
      units: Number(form.units), 
      teacher: Number(form.teacher) 
    };

    if (editingId !== null) {
      await onUpdateCourse(editingId, data);
      setEditingId(null);
    } else {
      await onAddCourse(data);
    }
    setForm({ course_name: '', units: '', teacher: '' });
  };

  return (
    <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
      <div className="p-6 border-b border-gray-100 bg-gray-50/50">
        <h3 className="text-lg font-bold text-gray-800">{editingId ? 'ðŸ“š Edit Course' : 'ðŸ“– Add New Course'}</h3>
      </div>
      
      <form onSubmit={handleSubmit} className="p-6 grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
        <div className="space-y-1">
          <label className="text-xs font-semibold text-gray-500 uppercase">Course Name</label>
          <input className="w-full border border-gray-300 p-2 rounded-lg focus:ring-2 focus:ring-blue-500 outline-none" value={form.course_name} onChange={e => setForm({...form, course_name: e.target.value})} placeholder="e.g. Data Mining" />
        </div>
        <div className="space-y-1">
          <label className="text-xs font-semibold text-gray-500 uppercase">Units</label>
          <input className="w-full border border-gray-300 p-2 rounded-lg focus:ring-2 focus:ring-blue-500 outline-none" type="number" value={form.units} onChange={e => setForm({...form, units: e.target.value})} placeholder="3" />
        </div>
        <div className="space-y-1">
          <label className="text-xs font-semibold text-gray-500 uppercase">Assign Teacher</label>
          <select className="w-full border border-gray-300 p-2 rounded-lg focus:ring-2 focus:ring-blue-500 outline-none bg-white" value={form.teacher} onChange={e => setForm({...form, teacher: e.target.value})}>
            <option value="">Select Teacher</option>
            {teachers.map(t => (
              <option key={t.teacher_id} value={t.teacher_id}>{t.teacher_name}</option>
            ))}
          </select>
        </div>
        <div className="flex gap-2">
          <button type="submit" className={`flex-1 py-2 px-4 rounded-lg font-bold text-white transition-all shadow-sm ${editingId ? 'bg-amber-500 hover:bg-amber-600' : 'bg-indigo-600 hover:bg-indigo-700'}`}>
            {editingId ? 'Update' : 'Add'}
          </button>
        </div>
      </form>

      <div className="overflow-x-auto">
        <table className="w-full text-left">
          <thead className="bg-gray-100 text-gray-600 text-sm uppercase">
            <tr>
              <th className="p-4 font-semibold">Course Name</th>
              <th className="p-4 font-semibold text-center">Units</th>
              <th className="p-4 font-semibold">Teacher ID</th>
              <th className="p-4 font-semibold text-center">Actions</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {courses.map(c => (
              <tr key={c.course_id} className="hover:bg-indigo-50/50 transition-colors">
                <td className="p-4 font-medium text-gray-800">{c.course_name}</td>
                <td className="p-4 text-center">
                  <span className="px-3 py-1 bg-indigo-100 text-indigo-700 rounded-full text-xs font-bold">{c.units} Units</span>
                </td>
                <td className="p-4 text-gray-500 font-mono text-sm">REF: {c.teacher}</td>
                <td className="p-4 text-center space-x-3">
                  <button onClick={() => { setEditingId(c.course_id); setForm({ course_name: c.course_name, units: String(c.units), teacher: String(c.teacher) }); }} className="text-indigo-600 hover:text-indigo-800 font-bold text-sm">Edit</button>
                  <button onClick={() => onDeleteCourse(c.course_id)} className="text-red-500 hover:text-red-700 font-bold text-sm">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CourseList;
