import React, { useState } from 'react';
import { Course, Teacher } from '../types';

interface CourseListProps {
  courses: Course[];
  teachers: Teacher[];
  onAddCourse: (course: Omit<Course, 'course_id'>) => Promise<void>;
  onUpdateCourse: (id: number, data: Partial<Course>) => Promise<void>;
  onDeleteCourse: (id: number) => Promise<void>;
}

export const CourseList: React.FC<CourseListProps> = ({ courses, teachers, onAddCourse, onUpdateCourse, onDeleteCourse }) => {
  const [form, setForm] = useState({ course_name: '', units: '', teacher: '' });
  const [editingId, setEditingId] = useState<number | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const data = { ...form, units: Number(form.units) };
    if (editingId !== null) {
      await onUpdateCourse(editingId, data);
      setEditingId(null);
    } else {
      await onAddCourse(data);
    }
    setForm({ course_name: '', units: '', teacher: '' });
  };

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', marginBottom: '20px' }}>
      <h3>{editingId ? 'Edit Course' : 'Add Course'}</h3>
      <form onSubmit={handleSubmit}>
        <input placeholder="Course" value={form.course_name} onChange={e => setForm({...form, course_name: e.target.value})} />
        <input placeholder="Units" type="number" value={form.units} onChange={e => setForm({...form, units: e.target.value})} />
        <select value={form.teacher} onChange={e => setForm({...form, teacher: e.target.value})}>
          <option value="">Select Teacher</option>
          {teachers.map(t => <option key={t.teacher_id} value={t.teacher_name}>{t.teacher_name}</option>)}
        </select>
        <button type="submit">{editingId ? 'Save' : 'Add'}</button>
      </form>
      <table border={1} style={{ width: '100%', marginTop: '10px' }}>
        <thead><tr><th>Course</th><th>Units</th><th>Teacher</th><th>Actions</th></tr></thead>
        <tbody>
          {courses.map(c => (
            <tr key={c.course_id}>
              <td>{c.course_name}</td>
              <td>{c.units}</td>
              <td>{c.teacher}</td>
              <td>
                <button onClick={() => {
                  setEditingId(c.course_id);
                  setForm({ course_name: c.course_name, units: String(c.units), teacher: c.teacher });
                }}>Edit</button>
                <button onClick={() => onDeleteCourse(c.course_id)} style={{color: 'red'}}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CourseList;
