import React, { useState } from 'react';
import { Enrollment, Student, Course } from '../types';

interface EnrollmentProps {
  enrollments: Enrollment[];
  students: Student[];
  courses: Course[];
  onAddEnrollment: (data: { student: number, course: number }) => Promise<void>;
  onDeleteEnrollment: (id: number) => Promise<void>;
}

export const EnrollmentList: React.FC<EnrollmentProps> = ({ enrollments, students, courses, onAddEnrollment, onDeleteEnrollment }) => {
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc' }}>
      <h3>New Enrollment</h3>
      <select value={selectedStudent} onChange={e => setSelectedStudent(e.target.value)}>
        <option value="">Student</option>
        {students.map(s => <option key={s.student_id} value={s.student_id}>{s.first_name} {s.last_name}</option>)}
      </select>
      <select value={selectedCourse} onChange={e => setSelectedCourse(e.target.value)}>
        <option value="">Course</option>
        {courses.map(c => <option key={c.course_id} value={c.course_id}>{c.course_name}</option>)}
      </select>
      <button onClick={() => onAddEnrollment({ student: Number(selectedStudent), course: Number(selectedCourse) })}>Enroll</button>

      <table border={1} style={{ width: '100%', marginTop: '10px' }}>
        <thead><tr><th>Student ID</th><th>Course ID</th><th>Actions</th></tr></thead>
        <tbody>
          {enrollments.map(e => (
            <tr key={e.enrollment_id}>
              <td>{e.student}</td>
              <td>{e.course}</td>
              <td><button onClick={() => onDeleteEnrollment(e.enrollment_id)} style={{color: 'red'}}>Unenroll</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EnrollmentList;
