import React, { useState } from 'react';
import { Course, Teacher } from '../types';

interface CourseListProps {
  courses: Course[];
  teachers: Teacher[];
  onAddCourse: (course: any) => Promise<void>;
  onUpdateCourse: (id: number, data: any) => Promise<void>;
  onDeleteCourse: (id: number) => Promise<void>;
}

export const CourseList: React.FC<CourseListProps> = ({ courses, teachers, onAddCourse, onUpdateCourse, onDeleteCourse }) => {
  const [form, setForm] = useState({ course_name: '', units: '', teacher: '' });
  const [editingId, setEditingId] = useState<number | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    // CRITICAL FIX: Convert 'teacher' and 'units' to Numbers before sending to Django
    const data = { 
      course_name: form.course_name, 
      units: Number(form.units), 
      teacher: Number(form.teacher) 
    };

    if (editingId !== null) {
      await onUpdateCourse(editingId, data);
      setEditingId(null);
    } else {
      await onAddCourse(data);
    }
    setForm({ course_name: '', units: '', teacher: '' });
  };

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', marginBottom: '20px' }}>
      <h3>{editingId ? 'Edit Course' : 'Add Course'}</h3>
      <form onSubmit={handleSubmit}>
        <input 
          placeholder="Course" 
          value={form.course_name} 
          onChange={e => setForm({...form, course_name: e.target.value})} 
        />
        <input 
          placeholder="Units" 
          type="number" 
          value={form.units} 
          onChange={e => setForm({...form, units: e.target.value})} 
        />
        
        {/* FIX: Set the value of the option to t.teacher_id */}
        <select 
          value={form.teacher} 
          onChange={e => setForm({...form, teacher: e.target.value})}
        >
          <option value="">Select Teacher</option>
          {teachers.map(t => (
            <option key={t.teacher_id} value={t.teacher_id}>
              {t.teacher_name}
            </option>
          ))}
        </select>
        
        <button type="submit">{editingId ? 'Save' : 'Add'}</button>
        {editingId && <button onClick={() => { setEditingId(null); setForm({course_name:'', units:'', teacher:''}); }}>Cancel</button>}
      </form>

      <table border={1} style={{ width: '100%', marginTop: '10px', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Course</th>
            <th>Units</th>
            <th>Teacher ID</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(c => (
            <tr key={c.course_id}>
              <td>{c.course_name}</td>
              <td>{c.units}</td>
              <td>{c.teacher}</td>
              <td>
                <button onClick={() => {
                  setEditingId(c.course_id);
                  // Ensure we store the teacher ID string in the form for the select dropdown
                  setForm({ course_name: c.course_name, units: String(c.units), teacher: String(c.teacher) });
                }}>Edit</button>
                <button onClick={() => onDeleteCourse(c.course_id)} style={{color: 'red', marginLeft: '5px'}}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CourseList;
