import React, { useEffect, useState } from 'react';
import { Student, Teacher, Course, Enrollment } from './types';
import * as api from './api';

// Import your components from the component folder
import StudentList from './component/StudentList';
import TeacherList from './component/TeacherList';
import CourseList from './component/CourseList';
import EnrollmentList from './component/EnrollmentList';

const App: React.FC = () => {
  const [students, setStudents] = useState<Student[]>([]);
  const [teachers, setTeachers] = useState<Teacher[]>([]);
  const [courses, setCourses] = useState<Course[]>([]);
  const [enrollments, setEnrollments] = useState<Enrollment[]>([]);

  // Load all data on startup
  useEffect(() => {
    refreshData();
  }, []);

  const refreshData = async () => {
    try {
      const [s, t, c, e] = await Promise.all([
        api.getStudents(),
        api.getTeachers(),
        api.getCourses(),
        api.getEnrollments()
      ]);
      setStudents(s);
      setTeachers(t);
      setCourses(c);
      setEnrollments(e);
    } catch (err) {
      console.error("Error loading data. Check Django server or CORS.", err);
    }
  };

  // ============= Student Handlers =============
  const handleAddStudent = async (data: Omit<Student, 'student_id'>) => {
    await api.createStudent(data);
    refreshData();
  };
  const handleUpdateStudent = async (id: number, data: Partial<Student>) => {
    await api.updateStudent(id, data);
    refreshData();
  };
  const handleDeleteStudent = async (id: number) => {
    await api.deleteStudent(id);
    refreshData();
  };

  // ============= Teacher Handlers =============
  const handleAddTeacher = async (data: Omit<Teacher, 'teacher_id'>) => {
    await api.createTeacher(data);
    refreshData();
  };
  const handleUpdateTeacher = async (id: number, data: Partial<Teacher>) => {
    await api.updateTeacher(id, data);
    refreshData();
  };
  const handleDeleteTeacher = async (id: number) => {
    await api.deleteTeacher(id);
    refreshData();
  };

  // ============= Course Handlers =============
  const handleAddCourse = async (data: Omit<Course, 'course_id'>) => {
    await api.createCourse(data);
    refreshData();
  };
  const handleUpdateCourse = async (id: number, data: Partial<Course>) => {
    await api.updateCourse(id, data);
    refreshData();
  };
  const handleDeleteCourse = async (id: number) => {
    await api.deleteCourse(id);
    refreshData();
  };

  // ============= Enrollment Handlers =============
  const handleAddEnrollment = async (data: { student: number, course: number }) => {
    await api.createEnrollment(data);
    refreshData();
  };
  const handleDeleteEnrollment = async (id: number) => {
    await api.deleteEnrollment(id);
    refreshData();
  };

  return (
    <div style={{ maxWidth: '1000px', margin: '0 auto', fontFamily: 'Arial' }}>
      <h1>University Enrollment System</h1>
      <hr />

      <StudentList 
        students={students} 
        onAddStudent={handleAddStudent} 
        onUpdateStudent={handleUpdateStudent} 
        onDeleteStudent={handleDeleteStudent} 
      />

      <TeacherList 
        teachers={teachers} 
        onAddTeacher={handleAddTeacher} 
        onUpdateTeacher={handleUpdateTeacher} 
        onDeleteTeacher={handleDeleteTeacher} 
      />

      <CourseList 
        courses={courses} 
        teachers={teachers} 
        onAddCourse={handleAddCourse} 
        onUpdateCourse={handleUpdateCourse} 
        onDeleteCourse={handleDeleteCourse} 
      />

      <EnrollmentList 
        enrollments={enrollments} 
        students={students} 
        courses={courses} 
        onAddEnrollment={handleAddEnrollment} 
        onDeleteEnrollment={handleDeleteEnrollment} 
      />
    </div>
  );
};

export default App;
