import React, { useState } from 'react';
import { Enrollment, Student, Course } from '../types';

export const EnrollmentList: React.FC<{ enrollments: Enrollment[], students: Student[], courses: Course[], onAddEnrollment: any, onDeleteEnrollment: any }> = ({ enrollments, students, courses, onAddEnrollment, onDeleteEnrollment }) => {
  const [s, setS] = useState('');
  const [c, setC] = useState('');

  const handleEnroll = async () => {
    if (!s || !c) return;
    await onAddEnrollment({ student: Number(s), course: Number(c) });
    setS(''); setC('');
  };

  return (
    <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
      <div className="p-6 border-b border-gray-100 bg-gray-50/50 flex justify-between items-center">
        <h3 className="text-lg font-bold text-gray-800">Enrollment Records</h3>
        <span className="bg-blue-100 text-blue-800 text-xs font-extrabold px-3 py-1 rounded-full uppercase">{enrollments.length} Records</span>
      </div>
      <div className="p-6 bg-blue-50/30 grid grid-cols-1 md:grid-cols-3 gap-4 items-end">
        <select className="border border-gray-300 p-2 rounded-lg bg-white" value={s} onChange={e => setS(e.target.value)}>
          <option value="">Select Student</option>
          {students.map(std => <option key={std.student_id} value={std.student_id}>{std.first_name} {std.last_name}</option>)}
        </select>
        <select className="border border-gray-300 p-2 rounded-lg bg-white" value={c} onChange={e => setC(e.target.value)}>
          <option value="">Select Course</option>
          {courses.map(crs => <option key={crs.course_id} value={crs.course_id}>{crs.course_name}</option>)}
        </select>
        <button onClick={handleEnroll} className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg shadow-md transition-all">Enroll</button>
      </div>
      <table className="w-full text-left">
        <thead className="bg-gray-100 text-gray-600 text-sm uppercase">
          <tr><th className="p-4 font-semibold">Student Reference</th><th className="p-4 font-semibold">Course Reference</th><th className="p-4 font-semibold text-center">Action</th></tr>
        </thead>
        <tbody className="divide-y divide-gray-100">
          {enrollments.map(e => (
            <tr key={e.enrollment_id} className="hover:bg-red-50/50 transition-colors">
              <td className="p-4 text-gray-700">Student #{e.student}</td>
              <td className="p-4 text-gray-700 font-medium">Course #{e.course}</td>
              <td className="p-4 text-center">
                <button onClick={() => onDeleteEnrollment(e.enrollment_id)} className="text-red-500 hover:text-red-700 font-bold underline text-sm">Unenroll</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
export default EnrollmentList;
