import React, { useState } from 'react';
import { Teacher } from '../types';

interface TeacherListProps {
  teachers: Teacher[];
  onAddTeacher: (teacher: Omit<Teacher, 'teacher_id'>) => Promise<void>;
  onUpdateTeacher: (id: number, data: Partial<Teacher>) => Promise<void>;
  onDeleteTeacher: (id: number) => Promise<void>;
}

export const TeacherList: React.FC<TeacherListProps> = ({ teachers, onAddTeacher, onUpdateTeacher, onDeleteTeacher }) => {
  const [form, setForm] = useState({ teacher_name: '', email: '' });
  const [editingId, setEditingId] = useState<number | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (editingId !== null) {
      await onUpdateTeacher(editingId, form);
      setEditingId(null);
    } else {
      await onAddTeacher(form);
    }
    setForm({ teacher_name: '', email: '' });
  };

  const handleEdit = (t: Teacher) => {
    setEditingId(t.teacher_id);
    setForm({ teacher_name: t.teacher_name, email: t.email });
  };

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', marginBottom: '20px' }}>
      <h3>{editingId ? 'Edit Teacher' : 'Add Teacher'}</h3>
      <form onSubmit={handleSubmit}>
        <input placeholder="Name" value={form.teacher_name} onChange={e => setForm({...form, teacher_name: e.target.value})} />
        <input placeholder="Email" value={form.email} onChange={e => setForm({...form, email: e.target.value})} />
        <button type="submit">{editingId ? 'Save' : 'Add'}</button>
      </form>
      <table border={1} style={{ width: '100%', marginTop: '10px' }}>
        <thead><tr><th>Name</th><th>Email</th><th>Actions</th></tr></thead>
        <tbody>
          {teachers.map(t => (
            <tr key={t.teacher_id}>
              <td>{t.teacher_name}</td>
              <td>{t.email}</td>
              <td>
                <button onClick={() => handleEdit(t)}>Edit</button>
                <button onClick={() => onDeleteTeacher(t.teacher_id)} style={{color: 'red'}}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TeacherList;
